import base64

def save_base64_pdf(base64_string, output_filename):
    """
    Decode a Base64 string and save it as a PDF file.
    
    :param base64_string: The Base64 encoded string representing the PDF file.
    :param output_filename: The name of the output file to save the decoded PDF.
    """
    # Decode the Base64 string
    pdf_data = base64.b64decode(base64_string)
    
    # Write the binary data to a file
    with open(output_filename, 'wb') as pdf_file:
        pdf_file.write(pdf_data)

    print(f"PDF file saved as {output_filename}")

# Example usage
base64_pdf_string = ""
output_file_name = 'BT.pdf'

save_base64_pdf(base64_pdf_string, output_file_name)
